mapper.select:

select * from service_order

  is_delete = 0

    and ssid = #{ssid,jdbcType=BIGINT}


    and sgid = #{sgid,jdbcType=BIGINT}


    and mid = #{mid,jdbcType=BIGINT}


    and service_provider_id = #{serviceProviderId,jdbcType=BIGINT}


    and no = #{no,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and service_begin_time = #{serviceBeginTime,jdbcType=TIMESTAMP}


    and service_reality_begin_time = #{serviceRealityBeginTime,jdbcType=TIMESTAMP}


    and goods_quantity = #{goodsQuantity,jdbcType=INTEGER}


    and cost = #{cost,jdbcType=DECIMAL}


    and practical_duration = #{practicalDuration,jdbcType=INTEGER}


    and dbid = #{dbid,jdbcType=BIGINT}


    and discount_amount = #{discountAmount,jdbcType=DECIMAL}


    and commission_rate = #{commissionRate,jdbcType=INTEGER}


    and cash_amount = #{cashAmount,jdbcType=DECIMAL}


    and status = #{status,jdbcType=TINYINT}


    and refund_status = #{refundStatus,jdbcType=TINYINT}


    and refund_platform_amount = #{refundPlatformAmount,jdbcType=DECIMAL}


    and refund_server_amount = #{refundServerAmount,jdbcType=DECIMAL}


    and order_type = #{orderType,jdbcType=TINYINT}


    and ee_id = #{eeId,jdbcType=BIGINT}


    and operator = #{operator,jdbcType=VARCHAR}


    and operation_log = #{operationLog,jdbcType=VARCHAR}


    and remarks = #{remarks,jdbcType=VARCHAR}


    and consult_content = #{consultContent,jdbcType=VARCHAR}


    and consult_name = #{consultName,jdbcType=VARCHAR}


    and consult_sex = #{consultSex,jdbcType=TINYINT}


    and consult_age = #{consultAge,jdbcType=INTEGER}


    and consult_phone = #{consultPhone,jdbcType=VARCHAR}


    and consult_profession = #{consultProfession,jdbcType=VARCHAR}


    and invisible_user = #{invisibleUser,jdbcType=TINYINT}


    and invisible_psycho = #{invisiblePsycho,jdbcType=TINYINT}


    and is_recommend = #{isRecommend,jdbcType=TINYINT}


    and recommand_price = #{recommandPrice,jdbcType=DECIMAL}


    and is_enable = #{isEnable,jdbcType=TINYINT}


    and notestatus = #{notestatus,jdbcType=TINYINT}


    and price = #{price,jdbcType=DECIMAL}


    and duration = #{duration,jdbcType=INTEGER}


    and order_source = #{orderSource,jdbcType=TINYINT}


    and apid = #{apid,jdbcType=BIGINT}


    and finish_time = #{finishTime,jdbcType=TIMESTAMP}


    and is_new_app = #{isNewApp,jdbcType=TINYINT}


    and noid = #{noid,jdbcType=BIGINT}


    and psycho_status = #{psychoStatus,jdbcType=TINYINT}


    and comsumer_status = #{comsumerStatus,jdbcType=TINYINT}


    and caid = #{caid,jdbcType=BIGINT}


    and parent_order_id = #{parentOrderId,jdbcType=BIGINT}
